import React, { useContext } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";

import { customHook } from "../hooks/customHook.jsx";
import { UserContext } from "../App/App";

const Register = () => {
  const history = useHistory();
  const email = customHook("email");
  const password = customHook("password");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("/api/auth/register", {
      email: email.value,
      password: password.value,
    })
    .then((res) => res.data)
    .then(() => history.push("/login"))
  };

  return (
    <div className="layout m-5">
      <h3 className="title is-3">New User</h3>
      <form onSubmit={handleSubmit}>
        <label className="label my-3">Nameconst email = customHook("email")</label>
        <input className="input" type="text" placeholder="Playlist name" />

        <label className="label my-3">Description</label>
        <input className="textarea" placeholder="Description" />
        <button className="button is-link my-5">Submit</button>
      </form>
    </div>
  );
};

export default Register;
