import axios from "axios";
import React from "react";
import CardFavorites from "./CardFavorites.jsx"
import { useParams } from "react-router-dom";

const Favorites = () => {

    const [favorites, setFavorites] = React.useState([])

    React.useEffect(() => {
        axios.get("/api/auth/me")
        .then((user) => user.data)
        .then((favorites)=> {
            setFavorites(favorites.favoritesID)
        })
    }, [])


    return(
        <div>
        {favorites.map((fav, i) => {
            <CardFavorites key={i} peli={fav} />
        })}
        </div>
    )
}

export default Favorites;
