export default function validateLogin(values) {
  const format = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+/;
  let errors = {};
  if (!values.username) {
    errors.username = "Nombre de usuario es requerido";
  } else if (format.test(values.username)) {
    errors.username = "Nombre de usuario contiene un caracter invalido";
  }
}
