export default function validateRegister(values) {
  const format = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+/;
  let errors = {};
  if (!values.username) {
    errors.username = "Nombre de usuario es requerido";
  } else if (format.test(values.username)) {
    errors.username = "Nombre de usuario contiene un caracter invalido";
  }
  if (!values.email) {
    errors.email = "Email es requerido";
  } else if (!/\S+@\S+\.\S+/.test(values.email)) {
    errors.email = "Email es invalido";
  }
  if (!values.password) {
    errors.password = "Contraseña es requerida";
  } else if (values.password.length < 6) {
    errors.password = "Contraseña tiene que tener minimo 6 caracteres";
  }
  if (!values.password2) {
    errors.password2 = "Contraseña es requerida";
  } else if (values.password2 !== values.password) {
    errors.password2 = "Las contraseñas no coinciden";
  }
  return errors;
}
