import axios from "axios";
import React from "react";
import CardFavorites from "./CardFavorites.jsx";

const Favorites = () => {
  const [favorites, setFavorites] = React.useState([]);

  const user = useSelector((state) => state.user);

  React.useEffect(() => {
    setFavorites(user.favoritesId);
  });

  return (
    <React.Fragment>
      {favorites.map((fav, i) => {
        return <CardFavorites key={i} movieID={fav} />;
      })}
    </React.Fragment>
  );
};

export default Favorites;
