import React from "react";
import axios from "axios";
import { useParams, useLocation } from "react-router-dom";
import { Fragment } from "react";
import { Popover, Transition } from "@headlessui/react";
import { MenuIcon, XIcon } from "@heroicons/react/outline";

const Movie = () => {
  const location = useLocation();
  const id = location.pathname.slice(7);

  const [movie, setMovie] = React.useState("");

  React.useEffect(() => {
    axios
      .get(`/api/movies/${id}`)
      .then((re) => re.data)
      .then((data) => setMovie(data));
  }, "");
  console.log(movie);

  /*  const movies = useParams();
  const [peliculas, setPeliculas] = React.useState([]);

  React.useEffect(() => {
    axios
      .get("/api/movies/search/:" + movies.id)
      .then((re) => re.data)
      .then((data) => {
        setPeliculas(data.results);
      });
  }, [movies]);
  console.log(peliculas); */

  return (
    <>
      <section className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-4 py-12">
        <div className="text-center pb-12">
          <h2 className="text-base font-bold text-indigo-600">
            {"myOMDB"}
          </h2>
          <h1 className="font-bold text-3xl md:text-4xl lg:text-5xl font-heading text-gray-900">
            {movie.title}
          </h1>
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className="w-full bg-white rounded-lg sahdow-lg overflow-hidden flex flex-col md:flex-row">
            <div className="w-full md:w-2/5 h-80">
              <img
                className="object-center object-cover w-full h-full"
                src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
                alt="photo"
              />
            </div>
            <div className="w-full md:w-3/5 text-left p-6 md:p-4 space-y-2">
              <p className="text-xl text-gray-700 font-bold">{movie.title}</p>
              <p className="text-base text-gray-400 font-normal">
              {movie.tagline}
              </p>
              <p className="text-base leading-relaxed text-gray-500 font-normal">
                {movie.overview}
              </p>
              <div className="flex justify-start space-x-2">
              <link
  rel="stylesheet"
  href="https://use.fontawesome.com/releases/v5.11.2/css/all.css"
/>
              <button
  class="
    bg-purple-500
    text-white
    active:bg-purple-600
    font-bold
    uppercase
    text-base
    px-8
    py-3
    rounded-full
    shadow-md
    hover:shadow-lg
    outline-none
    focus:outline-none
    mr-1
    mb-1
    ease-linear
    transition-all
    duration-150
  "
  type="button"
>
  <i class="fas fa-heart"></i>
</button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Movie;
