import React from "react";
import axios from "axios";
import {useParams, useLocation} from "react-router-dom"

const Movie = () => {
    const location = useLocation()
    console.log(location.pathname.slice(3))
   /*  const movies = useParams();
  const [peliculas, setPeliculas] = React.useState([]);

  React.useEffect(() => {
    axios
      .get("/api/movies/search/:" + movies.id)
      .then((re) => re.data)
      .then((data) => {
        setPeliculas(data.results);
      });
  }, [movies]);
  console.log(peliculas); */


  return (
    <>
      <div className="container mt-32 mx-auto p-4 md:p-0">
        <div className="shadow-lg flex flex-wrap w-full lg:w-4/5 mx-auto">
          <div
            className="bg-cover bg-bottom border w-full md:w-1/3 h-64 md:h-auto relative"
            style={{backgroundImage :`$url(https://images7.alphacoders.com/347/347549.jpg)`}}
          >
            <div className="absolute text-xl">
              <i className="fa fa-heart text-white hover:text-red-light ml-4 mt-4 cursor-pointer"></i>
            </div>
          </div>

          <div className="bg-white w-full md:w-2/3">
            <div className="h-full mx-auto px-6 md:px-0 md:pt-6 md:-ml-6 relative">
              <div className="bg-white lg:h-full p-6 -mt-6 md:mt-0 relative mb-4 md:mb-0 flex flex-wrap md:flex-wrap items-center">
                <div className="w-full lg:w-1/5 lg:border-right lg:border-solid text-center md:text-left">
                  <h3>Saint Basil's Cathedral</h3>
                  <p className="mb-0 mt-3 text-grey-dark text-sm italic">
                    Moscow - Russia
                  </p>
                  <hr className="w-1/4 md:ml-0 mt-4  border lg:hidden" />
                </div>

                <div className="w-full lg:w-3/5 lg:px-3">
                  <p className="text-md mt-4 lg:mt-0 text-justify md:text-left text-sm">
                    The Cathedral of Vasily the Blessed (Russian: Собор Василия
                    Блаженного, Sobor Vasiliya Blazhennogo), commonly known as
                    Saint Basil's Cathedral, is a church in Red Square in
                    Moscow, Russia. The building, now a museum, is officially
                    known as the Cathedral of the Intercession of the Most Holy
                    Theotokos on the Moat (Russian: Собор Покрова Пресвятой
                    Богородицы, что на Рву, Sobor Pokrova Presvyatoy
                    Bogoroditsy, chto na Rvu) or Pokrovsky Cathedral (Russian:
                    Покровский собор).[5] It was built from 1555–1561 on orders
                    from Ivan the Terrible and commemorates the capture of Kazan
                    and Astrakhan.
                  </p>
                </div>

                <div className="w-full lg:w-1/5 mt-6 lg:mt-0 lg:px-4 text-center md:text-left">
                  <button className="bg-white hover:bg-grey-darker hover:text-white border border-solid border-grey w-1/3 lg:w-full py-2">
                    Favoritos
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Movie;
