import axios from "axios";
import React from "react";
import { useLocation } from "react-router-dom";

const UserFinal = () => {
  const location = useLocation();
  const id = location.pathname.slice(7);

  const [favoritesId, setFavoritesId] = React.useState("");
  const [favorites, setFavorites] = React.useState("");

  React.useEffect(() => {
    axios
      .get(`/api/users/${id}`)
      .then((user) => user.data)
      .then((data) => setFavoritesId(data.favoritesID))
      .then(() =>{
          axios
              .get(`/api/movies/${favoritesId}`)
              .then((info) => info.data)
              .then((data) => setFavorites(data))
      })
  }, );
  console.log(favoritesId, favorites)


  /* React.useEffect(() => {
    axios
      .get(`/api/movies/${favoritesId}`)
      .then((info) => info.data)
      .then((data) => console.log(data));
  }); */

  return <></>;
};
export default UserFinal;
