import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";

const CardFavorites = ({ movieID, setFavorites }) => {
  const user = useSelector((state) => state.user);
  const [movie, setMovie] = React.useState([]);

  React.useEffect(() => {
    axios
      .get(`/api/movies/${movieID}`)
      .then((info) => info.data)
      .then((data) => {
        setMovie(data);
      });
    setFavorites(user.favoritesId);
  }, []);

  const handleClick = () => {
    axios.delete(`/api/favorites/${movie.id}`).then(window.location.reload());
  };

  return (
    <div className="p-10">
      <div className=" w-full lg:max-w-full lg:flex">
        <div
          className="h-48 lg:h-auto lg:w-48 flex-none bg-cover rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden"
          style={{
            backgroundImage: `url(https://image.tmdb.org/t/p/original/${movie.poster_path})`,
          }}
          title="Mountain"
        ></div>
        <div className="border-r border-b border-l border-gray-400 lg:border-l-1 lg:border-t lg:border-gray-400 bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
          <div className="mb-8">
            <div className="text-gray-900 font-bold text-xl mb-2">
              {movie.title}
            </div>
            <p className="text-gray-700 text-base">{movie.overview}</p>
            <link
              rel="stylesheet"
              href="https://use.fontawesome.com/releases/v5.11.2/css/all.css"
            />
            <button
              onClick={handleClick}
              className="mt-6 bg-purple-500 text-white active:bg-purple-600 font-bold uppercase text-base px-8 py-3 rounded-full shadow-md hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150 "
              type="button"
            >
              <i className="fas fa-heart-broken"></i>
            </button>
            <Link to={`/movie/${movieID}`}>
              <button
                className="mt-6 bg-purple-500 text-white active:bg-purple-600 font-bold uppercase text-base px-8 py-3 rounded-full shadow-md hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                type="button"
              >
                Ver pelicula
              </button>
            </Link>
          </div>
          <div className="flex items-center">
            <img
              className="w-10 h-10 rounded-full mr-4"
              src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
              alt="Avatar of Writer"
            />
            <div className="text-sm">
              <p className="text-gray-900 leading-none">
                Fecha de lanzamiento:
              </p>
              <p className="text-gray-600">{movie.release_date}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CardFavorites;
