import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";

const CardFavorites = ({ movieID }) => {
  const [movie, setMovie] = React.useState([]);
  React.useEffect(() => {
    axios
      .get(`/api/movies/${movieID}`)
      .then((re) => re.data)
      .then((data) => {
        setMovie(data);
      });
  }, []);

  const handleClick = () => {
      console.log(movie.id)
    axios(`/api/favorites/${movie.id}`).then(() =>
      alert(`Se elimino la pelicula`)
    );
  };

  return (
    <Link to={`/movie/${movieID}`}>
      <div className="p-10">
        <div className=" w-full lg:max-w-full lg:flex">
          <div
            className="h-48 lg:h-auto lg:w-48 flex-none bg-cover rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden"
            style={{
              backgroundImage: `url(https://image.tmdb.org/t/p/original/${movie.poster_path})`,
            }}
            title="Mountain"
          ></div>
          <div className="border-r border-b border-l border-gray-400 lg:border-l-1 lg:border-t lg:border-gray-400 bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
            <div className="mb-8">
              <p className="text-sm text-gray-600 flex items-center">
                {/* <svg className="fill-current text-gray-500 w-3 h-3 mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
              <path d="M4 8V6a6 6 0 1 1 12 0v2h1a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2v-8c0-1.1.9-2 2-2h1zm5 6.73V17h2v-2.27a2 2 0 1 0-2 0zM7 6v2h6V6a3 3 0 0 0-6 0z" />
            </svg> */}
              </p>
              <div className="text-gray-900 font-bold text-xl mb-2">
                {movie.title}
              </div>
              <p className="text-gray-700 text-base">{movie.overview}</p>
              <link
                rel="stylesheet"
                href="https://use.fontawesome.com/releases/v5.11.2/css/all.css"
              />
              <button
                onClick={handleClick}
                className="
                    bg-purple-500
                    text-white
                    active:bg-purple-600
                    font-bold
                    uppercase
                    text-base
                    px-8
                    py-3
                    rounded-full
                    shadow-md
                    hover:shadow-lg
                    outline-none
                    focus:outline-none
                    mr-1
                    mb-1
                    ease-linear
                    transition-all
                    duration-150
                    "
                type="button"
              >
                <i class="fas fa-heart-broken"></i>
              </button>
            </div>
            <div className="flex items-center">
              <img
                className="w-10 h-10 rounded-full mr-4"
                src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
                alt="Avatar of Writer"
              />
              <div className="text-sm">
                <p className="text-gray-900 leading-none">
                  Fecha de lanzamiento:
                </p>
                <p className="text-gray-600">{movie.release_date}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default CardFavorites;
